import React, { useState } from "react";
import "./AIIFAMembershipForm.css";

const defaultFormState = {
  companyName: "",
  factoryDistrict: "",
  factoryCity: "",
  factoryPhone: "",
  factoryPinCode: "",
  factoryMobile: "",
  factoryEmail1: "",
  factoryEmail2: "",
  correspondenceDistrict: "",
  correspondenceCity: "",
  correspondencePinCode: "",
  correspondenceMobile: "",
  correspondencePhone: "",
  correspondenceEmail1: "",
  correspondenceEmail2: "",
  representativeName: "",
  designation: "",
  representativeMobile: "",
  representativePinCode: "",
  representativeEmail: "",
  gstNumber: "",
  furnaceCapacity: "",
  furnaceMake: "",
  numberOfCrucible: "",
  bisNumber: "",
  productIngot: false,
  productBillet: false,
  bisYes: false,
  bisNo: false,
  bisAF: false,
  driIron: "",
  pelletIron: "",
  importedScrap: "",
  indigenousScrap: "",

  // Section 7
  dinManufacturerY: false,
  dinManufacturerN: false,
  processRouteGasBased: false,
  processRouteCoalBased: false,
  technology: "",
  annualCapacity: "",
  pelletProcess: "",
  pelletAnnualCapacity: "",

  // Section 8
  rollingMillIntegrated: false,
  rollingMillSisterConcern: false,

  // Section 9
  millCapacityRoughing: "",
  millCapacityIntermediate: "",
  millCapacityFinishing: "",

  // Section 10
  productTMT: false,
  productStructure: false,
  productOther: false,
  otherProductName: "",

  // Section 11
  brandName: "",
  powerConnectingLoad: "",

  // Section 12
  procurementSelfGenerating: false,
  procurementState: false,
  procurementOpenAccess: false,

  // Section 14
  bisCertificationMark: "",
};

const AIIFAMembershipForm = () => {
  const [successMessage, setSuccessMessage] = useState("");
  const [formData, setFormData] = useState(defaultFormState);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/api/membership", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok) {
        setSuccessMessage("Membership submitted successfully!");

        setTimeout(() => {
          setSuccessMessage("");
        }, 4000);

        setFormData(defaultFormState);
      } else {
        alert("Error: " + result.message);
      }
    } catch (error) {
      alert("Failed to submit form.");
    }
  };

  return (
    <div className="aiifa-form-container">

      {/* Success Message */}
      {successMessage && (
        <div className="success-box">{successMessage}</div>
      )}

      <form onSubmit={handleSubmit} className="membership-form">

        {/* ---------------------- SECTION 1–10 (unchanged code) ---------------------- */}
        {/* I am not rewriting your full form again, your code for 1–10 stays same */}
        {/* JUST PASTE YOUR EXISTING SECTION 1–10 CODE HERE */}

        {/* ---------------------- SECTION 11 ---------------------- */}
        <div className="form-section">
          <div className="section-number">11.</div>
          <div className="section-content">
            <div className="brand-power-grid">
              <div className="input-group">
                <span className="input-label">BRAND NAME:</span>
                <input
                  type="text"
                  name="brandName"
                  value={formData.brandName}
                  onChange={handleInputChange}
                />
              </div>

              <div className="input-group">
                <span className="input-label">
                  POWER CONNECTING LOAD (KVA):
                </span>
                <input
                  type="text"
                  name="powerConnectingLoad"
                  value={formData.powerConnectingLoad}
                  onChange={handleInputChange}
                />
              </div>
            </div>
          </div>
        </div>

        {/* ---------------------- SECTION 12 ---------------------- */}
        <div className="form-section">
          <div className="section-number">12.</div>
          <div className="section-content">
            <label className="section-label">PROCUREMENT OF POWER:</label>

            <div className="checkbox-group">
              <label>
                <input
                  type="checkbox"
                  name="procurementSelfGenerating"
                  checked={formData.procurementSelfGenerating}
                  onChange={handleInputChange}
                />
                Self Generating
              </label>

              <label>
                <input
                  type="checkbox"
                  name="procurementState"
                  checked={formData.procurementState}
                  onChange={handleInputChange}
                />
                State
              </label>

              <label>
                <input
                  type="checkbox"
                  name="procurementOpenAccess"
                  checked={formData.procurementOpenAccess}
                  onChange={handleInputChange}
                />
                Open Access
              </label>
            </div>
          </div>
        </div>

        {/* ---------------------- SECTION 14 ---------------------- */}
        <div className="form-section">
          <div className="section-number">14.</div>
          <div className="section-content">
            <label className="section-label">BIS CERTIFICATION MARK:</label>
            <input
              type="text"
              name="bisCertificationMark"
              value={formData.bisCertificationMark}
              onChange={handleInputChange}
              className="full-input"
            />
          </div>
        </div>

        {/* Submit Button */}
        <div className="submit-row">
          <button type="submit" className="submit-btn">
            Submit Membership Form
          </button>
        </div>
      </form>
    </div>
  );
};

export default AIIFAMembershipForm;
