import React from "react";

const AboutUsSection = () => {
  return (
    <section className="about-us-sec-container">
      <div className="about-us-sec-content">
        <p className="about-us-sec-subtitle">ABOUT US</p>
        <h2 className="about-us-sec-title">Know About our Organization</h2>
        <p className="about-us-sec-text">
          All India Induction Furnaces Association (AIIFA) being the premier steel
          industry association of Electric Induction Furnaces in the country represents
          a major section of steel units, producing steel through this route. There are
          over 1000 Induction furnace units and over 1200 steel rolling mill units
          scattered across all states in the country. These units produce around 30
          Million tonnes (MT) of steel annually, which is around 27% of the domestic
          crude steel production of the country. The sector accounts for direct
          employment of over 2 million people and supports livelihood for another 3
          million people.
        </p>
      </div>
    </section>
  );
};

export default AboutUsSection;
